mapper.update:
update guess_bet set
versusId = #{versusId}, bo = #{bo}, playTypeId = #{playTypeId}, userId = #{userId}, userName = #{userName}, betDirection = #{betDirection}, betAmount = #{betAmount}, status = #{status}, createTime = #{createTime}
where id = #{id}

mapper.select:
        select id, versusId, bo, playTypeId, userId, userName, betDirection, betAmount, status, createTime from guess_bet
        
            where
            
                
                    ${obj.field} ${obj.operator} #{obj.value}
                    ${obj.join} ${obj.field} ${obj.operator} #{obj.value}
                
            
        

        
            order by
            
                ${obj}
            
        

        limit 0, 1

