mapper.update:
update guess_bet set
versusId = #{versusId}, playTypeId = #{playTypeId}, userId = #{userId}, userName = #{userName}, betDirection = #{betDirection}, odds = #{odds}, betAmount = #{betAmount}, soldAmount = #{soldAmount}, soldOut = #{soldOut}, createTime = #{createTime}
where id = #{id}

mapper.select:
        select id, versusId, playTypeId, userId, userName, betDirection, odds, betAmount, soldAmount, soldOut, createTime from guess_bet
        
            where
            
                
                    ${obj.field} ${obj.operator} #{obj.value}
                    ${obj.join} ${obj.field} ${obj.operator} #{obj.value}
                
            
        

        
            order by
            
                ${obj}
            
        

        limit 0, 1

