mapper.update:
update guess_match set
sportId = #{sportId}, name = #{name}, logoUrl = #{logoUrl}, description = #{description}, startTime = #{startTime}, endTime = #{endTime}, status = #{status}, createTime = #{createTime}
where id = #{id}

mapper.select:
select id, sportId, name, logoUrl, description, startTime, endTime, status, createTime from guess_match

    where


            ${obj.field} ${obj.operator} #{obj.value}
            ${obj.join} ${obj.field} ${obj.operator} #{obj.value}





    order by

        ${obj}



limit 0, 1

