mapper.update:
update guess_match_versus set
name = #{name}, matchId = #{matchId}, matchName = #{matchName}, leftTeamId = #{leftTeamId}, leftTeamName = #{leftTeamName}, rightTeamId = #{rightTeamId}, rightTeamName = #{rightTeamName}, startTime = #{startTime}, status = #{status}, boCount = #{boCount}, realBoCount = #{realBoCount}, result = #{result}, createTime = #{createTime}
where id = #{id}

mapper.select:
select id, name, matchId, matchName, leftTeamId, leftTeamName, rightTeamId, rightTeamName, startTime, status, boCount, realBoCount, result, createTime from guess_match_versus

    where


            ${obj.field} ${obj.operator} #{obj.value}
            ${obj.join} ${obj.field} ${obj.operator} #{obj.value}





    order by

        ${obj}



limit 0, 1

