mapper.update:
update new_guess_versus_item set
name = #{name}, versusId = #{versusId}, betAmount = #{betAmount}, useFixedOdds = #{useFixedOdds}, fixedOdds = #{fixedOdds}, changeOdds = #{changeOdds}, changeOddsPlusRatio = #{changeOddsPlusRatio}, changeOddsPlusValue = #{changeOddsPlusValue}, changeOddsPlusStrategy = #{changeOddsPlusStrategy}, changeOddsMinusRatio = #{changeOddsMinusRatio}, changeOddsMinusValue = #{changeOddsMinusValue}, changeOddsMinusStrategy = #{changeOddsMinusStrategy}, changeOddsMin = #{changeOddsMin}, changeOddsMax = #{changeOddsMax}, createTime = #{createTime}
where id = #{id}

mapper.select:
        select id, name, versusId, betAmount, useFixedOdds, fixedOdds, changeOdds, changeOddsPlusRatio, changeOddsPlusValue, changeOddsPlusStrategy, changeOddsMinusRatio, changeOddsMinusValue, changeOddsMinusStrategy, changeOddsMin, changeOddsMax, createTime from new_guess_versus_item
        
            where
            
                
                    ${obj.field} ${obj.operator} #{obj.value}
                    ${obj.join} ${obj.field} ${obj.operator} #{obj.value}
                
            
        

        
            order by
            
                ${obj}
            
        

        limit 0, 1

        select id, name, versusId, betAmount, useFixedOdds, fixedOdds, changeOdds, changeOddsPlusRatio, changeOddsPlusValue, changeOddsPlusStrategy, changeOddsMinusRatio, changeOddsMinusValue, changeOddsMinusStrategy, changeOddsMin, changeOddsMax, createTime from new_guess_versus_item
         
            where
            
                
                    ${obj.field} ${obj.operator} #{obj.value}
                    ${obj.join} ${obj.field} ${obj.operator} #{obj.value}
                
            
        

         
            order by
            
                ${obj}
            
        

         
            limit ${page.beginIndex}, ${page.pageSize}
