mapper.update:
update new_guess_versus set
name = #{name}, logoUrl = #{logoUrl}, itemId = #{itemId}, itemName = #{itemName}, returnRate = #{returnRate}, betAmountMin = #{betAmountMin}, betAmountMax = #{betAmountMax}, betAllAmount = #{betAllAmount}, startTime = #{startTime}, endTime = #{endTime}, betPause = #{betPause}, resultItemId = #{resultItemId}, resultItemName = #{resultItemName}, status = #{status}, superVersusId = #{superVersusId}, childVersusCount = #{childVersusCount}, createTime = #{createTime}
where id = #{id}

mapper.select:
        select id, name, logoUrl, itemId, itemName, returnRate, betAmountMin, betAmountMax, betAllAmount, startTime, endTime, betPause, resultItemId, resultItemName, status, superVersusId, childVersusCount, createTime from new_guess_versus
        
            where
            
                
                    ${obj.field} ${obj.operator} #{obj.value}
                    ${obj.join} ${obj.field} ${obj.operator} #{obj.value}
                
            
        

        
            order by
            
                ${obj}
            
        

        limit 0, 1

        select id, name, logoUrl, itemId, itemName, returnRate, betAmountMin, betAmountMax, betAllAmount, startTime, endTime, betPause, resultItemId, resultItemName, status, superVersusId, childVersusCount, createTime from new_guess_versus
         
            where
            
                
                    ${obj.field} ${obj.operator} #{obj.value}
                    ${obj.join} ${obj.field} ${obj.operator} #{obj.value}
                
            
        

         
            order by
            
                ${obj}
            
        

         
            limit ${page.beginIndex}, ${page.pageSize}
