mapper.update:
update guess_play_type set
versusId = #{versusId}, name = #{name}, bo = #{bo}, paramStr = #{paramStr}, templateClass = #{templateClass}, createTime = #{createTime}
where id = #{id}

mapper.select:
        select id, versusId, name, bo, paramStr, templateClass, createTime from guess_play_type
        
            where
            
                
                    ${obj.field} ${obj.operator} #{obj.value}
                    ${obj.join} ${obj.field} ${obj.operator} #{obj.value}
                
            
        

        
            order by
            
                ${obj}
            
        

        limit 0, 1

