mapper.update:
update guess_play_type set
versusId = #{versusId}, playType = #{playType}, bo = #{bo}, params = #{params}, createTime = #{createTime}
where id = #{id}

mapper.select:
        select id, versusId, playType, bo, params, createTime from guess_play_type
        
            where
            
                
                    ${obj.field} ${obj.operator} #{obj.value}
                    ${obj.join} ${obj.field} ${obj.operator} #{obj.value}
                
            
        

        
            order by
            
                ${obj}
            
        

        limit 0, 1

