mapper.update:
update guess_play_type set
versusId = #{versusId}, name = #{name}, bo = #{bo}, versusBoId = #{versusBoId}, status = #{status}, result = #{result}, paramStr = #{paramStr}, templateClass = #{templateClass}, pause = #{pause}, guessStart = #{guessStart}, leftGuessName = #{leftGuessName}, rightGuessName = #{rightGuessName}, leftWinRate = #{leftWinRate}, rightWinRate = #{rightWinRate}, leftBonusPool = #{leftBonusPool}, rightBonusPool = #{rightBonusPool}, fixedOdds = #{fixedOdds}, createTime = #{createTime}
where id = #{id}

mapper.select:
        select id, versusId, name, bo, versusBoId, status, result, paramStr, templateClass, pause, guessStart, leftGuessName, rightGuessName, leftWinRate, rightWinRate, leftBonusPool, rightBonusPool, fixedOdds, createTime from guess_play_type
        
            where
            
                
                    ${obj.field} ${obj.operator} #{obj.value}
                    ${obj.join} ${obj.field} ${obj.operator} #{obj.value}
                
            
        

        
            order by
            
                ${obj}
            
        

        limit 0, 1

        select id, versusId, name, bo, versusBoId, status, result, paramStr, templateClass, pause, guessStart, leftGuessName, rightGuessName, leftWinRate, rightWinRate, leftBonusPool, rightBonusPool, fixedOdds, createTime from guess_play_type
         
            where
            
                
                    ${obj.field} ${obj.operator} #{obj.value}
                    ${obj.join} ${obj.field} ${obj.operator} #{obj.value}
                
            
        

         
            order by
            
                ${obj}
            
        

         
            limit ${page.beginIndex}, ${page.pageSize}
