mapper.update:
update guess_team set
sportId = #{sportId}, sportName = #{sportName}, name = #{name}, logoUrl = #{logoUrl}, description = #{description}, createTime = #{createTime}
where id = #{id}

mapper.select:
select id, sportId, sportName, name, logoUrl, description, createTime from guess_team

    where


            ${obj.field} ${obj.operator} #{obj.value}
            ${obj.join} ${obj.field} ${obj.operator} #{obj.value}





    order by

        ${obj}



limit 0, 1

