mapper.update:
update guess_user set
userName = #{userName}, passWord = #{passWord}, withdrawPassword = #{withdrawPassword}, balance = #{balance}, nickName = #{nickName}, qq = #{qq}, phone = #{phone}, email = #{email}, registTime = #{registTime}, lastLoginTime = #{lastLoginTime}, lastLoginIp = #{lastLoginIp}, createTime = #{createTime}
where id = #{id}

mapper.select:
select id, userName, passWord, withdrawPassword, balance, nickName, qq, phone, email, registTime, lastLoginTime, lastLoginIp, createTime from guess_user

    where


            ${obj.field} ${obj.operator} #{obj.value}
            ${obj.join} ${obj.field} ${obj.operator} #{obj.value}





    order by

        ${obj}



limit 0, 1

